<?php function uploadFile($filename,$path,$typelist=null){	//1. 获取上传文件的名字	$upfile = $_FILES[$filename];	if(empty($typelist)){		$typelist=array("image/gif","image/jpg","image/jpeg","image/png");//允许的文件类型	}	//$path="upload3"; //指定上传文件的保存路径（相对的）	$res=array("error"=>false);//存放返回的结果	//2.过滤上传文件件的错误号	if($upfile["error"]>0){		switch($upfile["error"]){			case 1: 				$res["info"]="上传的文件超过了 php.ini 中 upload_max_filesize 选项限制";				break;			case 2:				$res["info"]="上传文件的大小超过了 HTML 表单中 MAX_FILE_SIZE 选项";				break;			case 3:				$res["info"]="文件只有部分被上传";				break;			case 4:				$res["info"]="没有文件被上传";				break;			case 6:				$res["info"]="找不到临时文件夹。";				break;			case 7:				$res["info"]="文件写入失败";				break;			default:				$res["info"]="未知错误！";				break;		}		return $res;	}	//3.本次文件大小的限制	if($upfile["size"]>100000){		$res["info"]="上传文件过大！";		return $res;	}	//4. 过滤类型	if(!in_array($upfile["type"],$typelist)){		$res["info"]="上传类型不符！".$upfile["type"];		return $res;	}	//5. 初始化下信息(为图片产生一个随机的名字)	$fileinfo = pathinfo($upfile["name"]);	do{		$newfile = date("YmdHis").rand(1000,9999).".".$fileinfo["extension"];//随机产生一个的文件名	}while(file_exists($newfile));		//6. 执行上传处理	if(is_uploaded_file($upfile["tmp_name"])){		if(move_uploaded_file($upfile["tmp_name"],$path."/".$newfile)){			//将上传成功后的文件名赋给返回数组			$res["info"]=$newfile;			$res["error"]=true;			return $res;		}else{			$res["info"]="上传文件失败！";		}	}else{		$res["info"]="不是一个上传的文件！";	}	return $res;}